def icon_svg(icon_name: str):
    svg_path = IconPath.get(icon_name)
    if not svg_path:
        return ""
    svg_html = f'<svg viewBox="0 0 32 32" width="24" height="24" fill-rule="evenodd"><path d="{svg_path}"></path></svg>'
    return mark_safe(svg_html)
